import React, {useState, useEffect, TableRow} from "react";
import Recipe from  "./recipe.component";
import '../App.css';
import axios from 'axios';

import { makeStyles } from '@material-ui/core/styles';
import Radio from '@material-ui/core/Radio';
import RadioGroup from '@material-ui/core/RadioGroup';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import FormControl from '@material-ui/core/FormControl';
import FormLabel from '@material-ui/core/FormLabel';
import clsx from 'clsx';

const useStyles = makeStyles(theme => ({
    formControl: {
      margin: theme.spacing(3),
    },
}));

// Inspired by blueprintjs
function StyledRadio(props) {
    const classes = useStyles();

    return (
      <Radio
        className={classes.root}
        disableRipple
        color="default"
        checkedIcon={<span className={clsx(classes.icon, classes.checkedIcon)} />}
        icon={<span className={classes.icon} />}
        {...props}
      />
    );
  }
const Search = () => {

    const APP_ID = "69c97021";
    const APP_KEY = "aeb10d6b6fa84e615dc7361c08b1836f";

    const [recipes, setRecipes] = useState([]);
    const [search, setSearch] = useState("");
    const [query, setQuery] = useState('chicken');

    useEffect(() => {
      getRecipes();

    },[query]);

    const getRecipes = async () => {
      const response = await fetch(
        `https://api.edamam.com/search?q=${query}&app_id=${APP_ID}&app_key=${APP_KEY}`
      );
      const data = await response.json();
      setRecipes(data.hits);
      console.log(data.hits);
    };

    const updateSearch = e => {
      setSearch(e.target.value);
      console.log(search);
    }

    const getSearch = e => {
      e.preventDefault();
      setQuery(search);
    }

    const [value, setValue] = React.useState('female');

    const handleChange = event => {
        setValue(event.target.value);
    };
    return (
        <div className="APP">
            <form className="search-form" onSubmit={getSearch} >
            <input 
                className="search-bar" 
                type="text" 
                value={search}
                onChange={updateSearch} 
            />
            <button className="search-button" type="submit" >
                Search
            </button>
            </form>
            <div>
                <FormControl component="fieldset">
                    <FormLabel component="legend">Gender</FormLabel>
                    <RadioGroup defaultValue="female" aria-label="gender" name="customized-radios" value={value} onChange={handleChange}>
                        <FormControlLabel value="female" control={<StyledRadio />} label="Female" />
                        <FormControlLabel value="male" control={<StyledRadio />} label="Male" />
                        <FormControlLabel value="other" control={<StyledRadio />} label="Other" />
                        <FormControlLabel
                        value="disabled"
                        disabled
                        control={<StyledRadio />}
                        label="(Disabled option)"
                        />
                    </RadioGroup>
                </FormControl>
                <div className="foodGrid" >
                    {recipes.map(recipe  => (
                    <Recipe 
                        key = {recipe.recipe.uri} 
                        title={recipe.recipe.label} 
                        calories={recipe.recipe.calories} 
                        image={recipe.recipe.image} 
                    />
                    ))}
                </div>
            </div>
        </div>
    );

  }
export default Search;
