            search:'',
            query:'',
        };
        console.log('init search: ' + this.state.search);
    }

    updateSearch = e => {

        console.log(search);
    }

    getSearch = e => {
        e.preventDefault();
        this.setState({
            search: e.target.value,
        });
        console.log('init search: ' + this.state.search);
    }
    render(){
        return (
            <div className="APP">
              <form className="search-form" onSubmit={getSearch} >
                <input 
                  className="search-bar" 
                  type="text" 
                  value={search}
                  onChange={updateSearch} 
                />
                <button className="search-button" type="submit" >
                  Search
                </button>
              </form>

              <UserList />
            </div>
          );
    }


}
