import React, { Component , useEffect} from 'react';
import UserList from './users-list.component';
import axios from 'axios';
const APP_ID = "69c97021";
const APP_KEY = "aeb10d6b6fa84e615dc7361c08b1836f";

export default class Search extends Component{

    constructor(){
        super();
        this.state = {
            search:'chicken',

        };
        console.log('init search: ' + this.state.search);
        this.updateSearch = this.updateSearch.bind();
        this.getSearch = this.getSearch.bind();
        this.getRecipes = this.getRecipes.bind();
    }
    componentDidMount(){
        useEffect = (() => {
            this.getRecipes();

          },[]);
    }


    getRecipes = async () => {
        const response = await fetch(
          `https://api.edamam.com/search?q=${this.state.search}&app_id=${APP_ID}&app_key=${APP_KEY}`
        );
        const data = await response.json();

        this.setState ({
            search : data.hits
        });

        console.log(data.hits);
    };

    updateSearch = e => {
        e.preventDefault();
        this.setState({
            search: e.target.value,
        });
        console.log(e.target.value);
    }

    getSearch = e => {
        e.preventDefault();
        this.setState({
            search: e.target.value,
        });
        console.log('end search: ' + this.state.search);

    }
    render(){
        return (
            <div className="APP">
              <form className="search-form" onSubmit={this.getSearch} >
                <input 
                  className="search-bar" 
                  type="text" 
                  value={this.state.search ||""}
                  onChange={this.updateSearch} 
                />
                <button className="search-button" type="submit" >
                    Search
                </button>
            </form>
            <div className="foodGrid" >
                {this.state.search.map(recipe  => (
                    <div className="recipeItem">
                        <h1>{recipe.title}</h1>
                        <p><strong>calories(KJ):</strong>{recipe.calories}</p>
                        <img src={recipe.image}  alt=""/>
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                        <br />
                    </div>
                ))}
            </div>
            </div>
          );
    }


}
